function populateDeleteQuestionId(n){$.ajax({type:"POST",url:"/Questions/DeleteDetails/"+n,cache:!1,success:function(n){$("#spanQuestionTitle").html(n.questionTitle.toString())},error:function(){alert("Ein Fehler ist aufgetreten")}})}function deleteQuestion(n){$.ajax({type:"POST",url:"/Questions/Delete/"+n,cache:!1,success:function(){window.location.reload()},error:function(n){console.log(n),alert("Ein Fehler ist aufgetreten")}})}var questionIdToDelete;$(function(){$("a[href*=#modalDelete]").click(function(){questionIdToDelete=$(this).attr("data-questionId"),populateDeleteQuestionId(questionIdToDelete)}),$("#btnCloseQuestionDelete").click(function(){$("#modalDelete").modal("hide")}),$("#confirmQuestionDelete").click(function(){deleteQuestion(questionIdToDelete),$("#modalDelete").modal("hide")})})